import process from 'node:process';
import cac from 'cac';

const name = "@condorhero/vuepress-plugin-export-pdf-core";
const type = "module";
const version = "3.0.3";
const description = "The Core of VuePress and VitePress exports PDF plugin";
const author = "CondorHero";
const license = "MIT";
const homepage = "https://github.com/condorheroblog/vuepress-plugin-export-pdf#readme";
const repository = {
	type: "git",
	url: "git+https://github.com/condorheroblog/vuepress-plugin-export-pdf.git",
	directory: "packages/vuepress-plugin-export-pdf-core"
};
const bugs = {
	url: "https://github.com/condorheroblog/vuepress-plugin-export-pdf/issues"
};
const keywords = [
	"vuepress-plugin-export-pdf",
	"vuepress-plugin",
	"vuepress",
	"plugin"
];
const exports = {
	".": {
		"import": "./dist/index.mjs",
		require: "./dist/index.cjs"
	}
};
const main = "dist/index.cjs";
const module = "dist/index.mjs";
const types = "dist/index.d.ts";
const typesVersions = {
	"*": {
		"*": [
			"./dist/*",
			"./dist/index.d.ts"
		]
	}
};
const files = [
	"dist"
];
const engines = {
	node: ">=8.6"
};
const scripts = {
	build: "unbuild",
	prepublishOnly: "nr build",
	dev: "esno ./src/index.ts"
};
const dependencies = {
	"@condorhero/merge-pdfs": "^0.0.7",
	"bundle-require": "^4.1.0",
	cac: "^6.7.14",
	envinfo: "^7.13.0",
	"fs-extra": "^11.2.0",
	"html-export-pdf-cli": "^1.1.5",
	multimatch: "^7.0.0",
	ora: "^8.0.1",
	pdfjs: "^2.5.3",
	picocolors: "^1.0.0",
	semver: "^7.6.2"
};
const devDependencies = {
	"@types/envinfo": "^7.8.3",
	"@types/fs-extra": "^11.0.4",
	"@types/semver": "^7.5.8"
};
const publishConfig = {
	access: "public",
	registry: "https://registry.npmjs.org"
};
const pkg = {
	name: name,
	type: type,
	version: version,
	description: description,
	author: author,
	license: license,
	homepage: homepage,
	repository: repository,
	bugs: bugs,
	keywords: keywords,
	exports: exports,
	main: main,
	module: module,
	types: types,
	typesVersions: typesVersions,
	files: files,
	engines: engines,
	scripts: scripts,
	dependencies: dependencies,
	devDependencies: devDependencies,
	publishConfig: publishConfig
};

function beforeParse(cliInstance) {
  cliInstance.version(pkg.version).help();
}
function afterParse(cliInstance) {
  if (!process.argv.slice(2).filter(Boolean).length)
    cliInstance.outputHelp();
}
function runCli(programName) {
  try {
    const program = cac(programName);
    return (registerCommands) => {
      registerCommands(program);
      beforeParse(program);
      program.parse(process.argv);
      afterParse(program);
    };
  } catch (error) {
    process.exit(1);
  }
}

export { afterParse, beforeParse, runCli };
