import { SingleBar } from 'cli-progress';

declare function createProgress(indeterminate?: boolean): {
    bar: SingleBar;
    start(total: number): void;
    updateNumber(v: number): void;
    increment(step: number, { txt, headTitle }?: {
        txt?: string;
        headTitle?: string;
    }): void;
    updateText(t: string): void;
    updateTitle(t: string): void;
    stop(): void;
};

export { createProgress };
